import { __decorate, __param } from "tslib";
import { AgendaEditComponent } from './../agenda-edit/agenda-edit.component';
import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
let AgendaPetComponent = class AgendaPetComponent {
    constructor(data, modal, service, _snackBar) {
        this.data = data;
        this.modal = modal;
        this.service = service;
        this._snackBar = _snackBar;
    }
    ngOnInit() {
        console.log(this.data);
    }
    editAgenda() {
        const editarAgenda = this.modal.open(AgendaEditComponent, { data: { pet: this.data.pet } });
    }
    async deletAgenda() {
        let loginStorage = localStorage.getItem('login');
        loginStorage = JSON.parse(loginStorage);
        (await this.service.updatePet(loginStorage.user, this.data.pet.nome)).subscribe(pet => {
            this._snackBar.open(pet.message, '', {
                horizontalPosition: 'center',
                verticalPosition: 'top',
                duration: 2500
            });
            if (pet.success) {
                this.dialogRef.close();
                // window.location.reload();
            }
        });
    }
};
