    Component({
        selector: 'app-agenda-pet',
        templateUrl: './agenda-pet.component.html',
        styleUrls: ['./agenda-pet.component.css']
    }),
    __param(0, Inject(MAT_DIALOG_DATA))
], AgendaPetComponent);
export { AgendaPetComponent };
