import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
let AddPetComponent = class AddPetComponent {
    constructor(router, service, _snackBar, modal, dialogRef) {
        this.router = router;
        this.service = service;
        this._snackBar = _snackBar;
        this.modal = modal;
        this.dialogRef = dialogRef;
    }
    ngOnInit() {
        this.formulario = new FormGroup({
            nome: new FormControl(''),
            idade: new FormControl(''),
            raca: new FormControl(''),
            imagem: new FormControl('')
        });
        console.log(this.service.getPets());
    }
    async createAgenda() {
        (await this.service.createPet({
            nome: this.formulario.value.nome,
            idade: this.formulario.value.idade,
            raca: this.formulario.value.raca,
            imagem: this.formulario.value.imagem
        })).subscribe(pet => {
            this._snackBar.open(pet.message, '', {
                horizontalPosition: 'center',
                verticalPosition: 'top',
                duration: 2500
            });
            if (pet.success) {
                this.dialogRef.close();
            }
        });
    }
};
AddPetComponent = __decorate([
    Component({
        selector: 'app-add-pet',
        templateUrl: './add-pet.component.html',
        styleUrls: ['./add-pet.component.css']
    })
], AddPetComponent);
export { AddPetComponent };
