    Component({
        selector: 'app-agenda-edit',
        templateUrl: './agenda-edit.component.html',
        styleUrls: ['./agenda-edit.component.css']
    }),
    __param(0, Inject(MAT_DIALOG_DATA))
], AgendaEditComponent);
