import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
let CadastroComponent = class CadastroComponent {
    constructor(router, service, _snackBar) {
        this.router = router;
        this.service = service;
        this._snackBar = _snackBar;
        this.hide = true;
    }
    ngOnInit() {
        this.formulario = new FormGroup({
            nome: new FormControl(''),
            login: new FormControl(''),
            telefone: new FormControl(''),
            email: new FormControl(''),
            dataNasc: new FormControl(''),
            senha: new FormControl('')
        });
        console.log(this.service.getClients());
    }
    async createClient() {
        (await this.service.createClient({
            nome: this.formulario.value.nome,
            login: this.formulario.value.login,
            telefone: this.formulario.value.telefone,
            email: this.formulario.value.email,
            dataNasc: this.formulario.value.dataNasc,
            senha: this.formulario.value.senha
        })).subscribe(client => {
            if (client.success) {
                this.router.navigate(['/home']).then(() => {
                    window.location.reload();
                });
            }
            else {
                this._snackBar.open(client.message, '', {
                    horizontalPosition: 'center',
                    verticalPosition: 'top',
                    duration: 2500
                });
            }
        });
    }
};
CadastroComponent = __decorate([
    Component({
        selector: 'app-cadastro',
        templateUrl: './cadastro.component.html',
        styleUrls: ['./cadastro.component.css']
    })
], CadastroComponent);
export { CadastroComponent };
